const input = $input.first().json.body.events[0].postback.data;

// Use a single regex to capture key-value pairs for both long (--key=value) and short (-k=value) options
const regex = /([a-zA-Z0-9_]+)=([^\s]*)/gu;

let matches;
let drug_type = null;
let drug_type_tmp = null;
let drug_number = null;
let drug_number_tmp = null;
let drug_datetime = null;
let pain_level = null;
let headache_occur = null;
let headache_occur_tmp = null;
let action_occur = null;
let action = null;
let no_headache_occur = null;

// Loop through the matches
while ((matches = regex.exec(input)) !== null) {
    const key = matches[1] || matches[3]; // Get long option name or short key
    var value = matches[2] || matches[4]; // Get the associated value
    value = value.trimEnd()
    // Assign to the appropriate variable based on the key
    if (key === 'drug_type') {
        drug_type = value;
    } else if (key === 'drug_number') {
        drug_number = value;
    } else if (key === 'drug_datetime') {
      drug_datetime = value;
    } else if (key === 'drug_type_tmp') {
      drug_type_tmp = value
    } else if (key === 'drug_number_tmp') {
      drug_number_tmp = value
    } else if (key === 'pain_level') {
      pain_level = value
    } else if (key === 'headache_occur') {
      headache_occur = value
    } else if (key === 'headache_occur_tmp') {
      headache_occur_tmp = value
    } else if (key === 'action_occur') {
      action_occur = value
    } else if (key === 'action') {
      action = value
    } else if (key === 'no_headache_occur') {
      no_headache_occur = value
    }
}

// Return the results
return { drug_datetime, drug_number, drug_type, drug_type_tmp, drug_number_tmp, pain_level, headache_occur, headache_occur_tmp, action_occur, action, no_headache_occur };

