let data = $input.all();
let formattedOutput = "";
for (let i = 0; i < data.length; i++) {
  const entry = data[i].json;
  console.log(entry)
  formattedOutput += `在 ${entry.drug_datetime} 吃了 ${"💊".repeat(entry.drug_number)} ${entry.drug_type}`;

  // Add newline if not the last element
  if (i < data.length - 1) {
    formattedOutput += '\n';
  }
}

formattedOutput = JSON.stringify(formattedOutput)
return {formattedOutput}

